mapper.select:
SELECT USERID, USERPW, USERNAME
FROM MP_MEMBER
WHERE USERID = #{userId}
AND   USERPW = #{userPw}

mapper.update:
	UPDATE MP_MEMBER SET
	USERPW =    #{userPw}
   ,USERNAME = #{userName}
    WHERE USERID = #{userId}

mapper.delete:
DELETE FROM MP_MEMBER
WHERE USERID = #{userId}
AND   USERPW = #{userPw}

mapper.select:
SELECT COUNT(*) FROM MP_MEMBER
WHERE USERID = #{userId}
AND   USERPW = #{userPw}
